/**
 * Bootstrap wrapper - redirects to CDN version
 * This file exists to prevent 404 errors, but the actual functionality
 * is loaded from the CDN in the footer.php file.
 */

// Check if Bootstrap is already loaded
if (typeof bootstrap === 'undefined') {
    console.warn('Bootstrap not loaded. Loading from CDN...');
    
    // Create script element to load Bootstrap from CDN
    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/js/bootstrap.bundle.min.js';
    script.integrity = 'sha384-ka7Sk0Gln4gmtz2MlQnikT1wXgYsOg+OMhuP+IlRH9sENBO0LRn5q+8nbTov4+1p';
    script.crossOrigin = 'anonymous';
    
    // Add event listeners for load and error
    script.addEventListener('load', function() {
        console.log('Bootstrap loaded successfully from CDN');
    });
    
    script.addEventListener('error', function() {
        console.error('Failed to load Bootstrap from CDN');
    });
    
    // Append script to document head
    document.head.appendChild(script);
} else {
    console.log('Bootstrap is already loaded.');
} 