/**
 * jQuery wrapper - redirects to CDN version
 * This file exists to prevent 404 errors, but the actual functionality
 * is loaded from the CDN in the footer.php file.
 */

// Check if jQuery is already loaded
if (typeof jQuery === 'undefined') {
    console.warn('jQuery not loaded. Loading from CDN...');
    
    // Create script element to load jQuery from CDN
    const script = document.createElement('script');
    script.src = 'https://code.jquery.com/jquery-3.6.0.min.js';
    script.integrity = 'sha256-/xUj+3OJU5yExlq6GSYGSHk7tPXikynS7ogEvDej/m4=';
    script.crossOrigin = 'anonymous';
    
    // Add event listeners for load and error
    script.addEventListener('load', function() {
        console.log('jQuery loaded successfully from CDN');
    });
    
    script.addEventListener('error', function() {
        console.error('Failed to load jQuery from CDN');
    });
    
    // Append script to document head
    document.head.appendChild(script);
} else {
    console.log('jQuery is already loaded. Version: ' + jQuery.fn.jquery);
} 